import {
  Button,
  Pressable,
  StyleSheet,
  Text,
  TextInput,
  View,
} from "react-native";
import { Colors } from "../../styles/colors";
import { globalStyles } from "../../styles/global";

const RowOptions = (options) => {
  return (
    <View style={styles.modalRow}>
      <View
        style={[
          options === "High" && {borderColor: Colors.red},
          options === "Middle" && {borderColor: Colors.yellow},
          options === "Low" && {borderColor: Colors.green},
          options === "None" && {borderColor: Colors.blue},
          styles.optionCircle,
        ]}
      />
      <Text style={styles.optionTxt}>{options}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  modalRow: {
    display: "flex",
    flexDirection: "row",
	 alignItems: 'center'
  },
  optionCircle: {
    overflow: "hidden",
    width: 20,
    height: 20,
    borderWidth: 5,
    borderRadius: 8,
	 marginRight: 5,
  },
  optionTxt: {
	  fontSize: 24,
	  fontWeight: '500',
	  color: Colors.black
  }
});

export default RowOptions;
