import React, { useEffect } from "react";
import {
  View,
  StyleSheet,
  Text,
  TextInput,
  Pressable,
  Button,
} from "react-native";
import { Header } from "../components/Header";
import List from "../components/List/List";
import TodoForm from "../components/TodoForm";
import { getTodosDB } from "../http/todoAPI";
import { logout } from "../http/userAPI";
import { ITodo } from "../interfaces/ITodo";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import {
  fetchTodoError,
  fetchTodos,
  fetchTodosSuccess,
} from "../store/todoSlice";
import { userLogout } from "../store/userSlice";

const Todo = () => {
  const { loading, user, userError } = useAppSelector((state) => state.user);
  const { loadingTodos, todos, todoError } = useAppSelector(
    (state) => state.todo
  );
  const dispatch = useAppDispatch();

  const onLogout = async () => {
    logout();
    dispatch(userLogout());
  };

  useEffect(() => {
    const getTodosFromDB = async () => {
      try {
        if (!!user) {
          dispatch(fetchTodos());
          const todos = await getTodosDB(user);
          dispatch(fetchTodosSuccess(todos));
        }
      } catch (e: any) {
        console.log(e.response.data.message);
        dispatch(fetchTodoError(e));
      }
    };
    getTodosFromDB();
  }, []);

  return (
    <View>
		 <Header/>
      {!!todoError && <Text>{todoError}</Text>}
      {!!userError && <Text>{userError}</Text>}
      {!!loading || (!!loadingTodos && <Text>Loading...</Text>)}
      <Text style={styles.title}>TODO LIST</Text>

      <TodoForm />
      <List todos={todos} />
      <Button title="Log out" onPress={onLogout} />
    </View>
  );
};


const styles = StyleSheet.create({

	title: {
	  fontSize: 30,
	  fontWeight: "700",
	  color: "#e4e3e3",
	},
})
export default Todo;
