import React, { useEffect, useState } from 'react'
import { StyleSheet,Button, Text, TextInput, View, Pressable } from "react-native";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { logout } from "../http/userAPI";
import { userLogout } from "../store/userSlice";
import ModalDropdown from 'react-native-modal-dropdown';
import { sortByDate, sortByPriority } from '../store/todoSlice';
import dayjs from 'dayjs';
import { globalStyles } from '../styles/global';

const sortOptions = ['Date', 'Priority']

export const Header = () => {
	const [sort, setSort] = useState<string>('')
	const dispatch = useAppDispatch();

	const onLogout = async () => {
	  logout();
	  dispatch(userLogout());
	};
	
	useEffect(() => {
		if (sort === 'Priority') {
			dispatch(sortByPriority())
			return
		}
		if (sort === 'Date') {
			dispatch(sortByDate())
			return
		}
	}, [sort])
  return (
	 <View style={[styles.container, globalStyles.primaryBg]}>
		 <View style={styles.titleWrapper}><Text style={[styles.title, globalStyles.grayTxt]}>Today {dayjs().format('DD MMM')}</Text></View>
		 <ModalDropdown
            defaultValue="Sort by"
				renderButtonText={() => 'Sort by'}
				textStyle={[globalStyles.blackTxt, {fontSize: 14}]}
            options={sortOptions}
            onSelect={(idx, value) => setSort(value)}
				style={[styles.sortModal, globalStyles.background]}
          />
      <Pressable onPress={onLogout}>
			<Text>Exit</Text>
		</Pressable>
		
	 </View>
  )
}


const styles = StyleSheet.create({
	container: {
	  display: "flex",
	  flexDirection: "row",
	  alignItems: "center",
	  borderBottomWidth: 1,
	  borderRadius: 5,
	  borderColor: '#F8F7FF',
	  paddingTop: 30,
	  paddingHorizontal: 10,
	  paddingBottom: 10,
	  width: "100%",
	},
	titleWrapper: {
		flex: 1,
	},
	title: {
		fontSize: 16,
		fontWeight: '500',
	},
	sortModal: {
		borderWidth: 1,
		borderRadius: 5,
		borderColor: '#F8F7FF',
		marginRight: 10,
		paddingVertical: 3,
		paddingHorizontal: 5,
		
	}
 });
