import React, { FC } from "react";
import { StyleSheet, Text, View } from "react-native";
import { ITodo } from "../interfaces/ITodo";
import Checkbox from "react-native-bouncy-checkbox";

interface TodoProps {
  item: ITodo;
}
const ListItem: FC<TodoProps> = ({ item }) => {
  const { checked, title } = item;
  return (
    <View style={styles.container}>
      <Checkbox
        isChecked={checked}
        useNativeDriver={false}
		  fillColor='#6200ff'
		  unfillColor="#fff"
			textC
      />
      {/* <Text style={styles.title}>{title}</Text> */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    borderColor: "#e4e3e3",
    borderWidth: 1,
    borderRadius: 10,
    marginBottom: 15,
    padding: 10,
    height: 40,
    width: "100%",
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
    color: "#e4e3e3",
  },
  input: {
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    width: "100",
    height: 50,
    marginBottom: 15,
  },
});

export default ListItem;
