import React, { useEffect } from "react";
import {
  View,
  StyleSheet,
  Text,
  TextInput,
  Pressable,
  Button,
} from "react-native";
import List from "../components/List";
import TodoForm from "../components/TodoForm";
import { getTodosDB } from "../http/todoAPI";
import { logout } from "../http/userAPI";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import {
  fetchTodoError,
  fetchTodos,
  fetchTodosSuccess,
} from "../store/todoSlice";
import { userLogin, userLoginSuccess, userLogout } from "../store/userSlice";

const Todo = () => {
  const { loading, user, userError } = useAppSelector((state) => state.user);
  const { loadingTodos, todos, todoError } = useAppSelector(
    (state) => state.todo
  );
  const dispatch = useAppDispatch();

  const onLogout = async () => {
   logout();
    dispatch(userLogout());
  };

  useEffect(() => {
	const checkAuth = async () => {
		try {
		 dispatch(userLogin());
		 const user = await check()
		 console.log(user);
		 
		 dispatch(userLoginSuccess(user))
		} catch (e: any) {
		 console.log(e.response.data.message);
		 dispatch(fetchUserError(e.response.data.message));
		}
	 
	}
	checkAuth()
}, []);

  useEffect(() => {
    const getTodosFromDB = async () => {
      try {
        dispatch(fetchTodos());
        const todos = user && (await getTodosDB(user));
        dispatch(fetchTodosSuccess(todos));
      } catch (e: any) {
        dispatch(fetchTodoError(e));
      }
    };
    getTodosFromDB();
  }, []);



  return (
    <View>
      {!!todoError && <Text>{todoError}</Text>}
      {!!userError && <Text>{userError}</Text>}
      {!!loading || (!!loadingTodos && <Text>Loading...</Text>)}
      <TodoForm />
      <List todos={todos} />
      <Button title="Log out" onPress={onLogout} />
    </View>
  );
};

export default Todo;
