import AsyncStorage from "@react-native-async-storage/async-storage";
import axios, { AxiosRequestConfig } from "axios";

const TODO_APP_API_URL = "http://localhost:4000";

export const instance = axios.create({
  baseURL: TODO_APP_API_URL,
});
export const auinstance = axios.create({
	baseURL: TODO_APP_API_URL,
 });

const authInterceptors = (config: AxiosRequestConfig) => {
	config.headers!.Authorization = `Bearer ${AsyncStorage.getItem('token')}`
}

instance.interceptors.request.use(authInterceptors)
