import {
	Button,
	Pressable,
	StyleSheet,
	Text,
	TextInput,
	View,
 } from "react-native";
import { globalStyles } from "../../styles/global";


  const RowOptions = (options) => {
	return (
	  <View style={styles.modalRow}>
			 <View style={[
			  options === "High" && globalStyles.redBg,
			  options === "Middle" && globalStyles.yellowBg,
			  options === "None" && globalStyles.blueBg,
			  options === "Low" && globalStyles.greenBg,
			 styles.optionCircle
			]}/>
		 <Text>{options}</Text>
	  </View>
	);
 }

 const styles = StyleSheet.create({
	modalRow: {
		display: "flex",
		flexDirection: "row",
	 },
  optionCircle: {
	overflow: 'hidden',
	width: 10,
	height: 10,
	borderWidth: 1,
	borderRadius: 50
  },
 

 });

 export default RowOptions
