import React, { useState } from "react";
import {
  View,
  StyleSheet,
  Text,
  TextInput,
  Pressable,
  Alert,
} from "react-native";
import { registration, login } from "../http/userAPI";
import { useAppSelector } from "../store/hooks";

const Auth = () => {
	const {user} = useAppSelector(state => state.user)
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLogin, setIsLogin] = useState(true);

  const onSubmit = async () => {
    try {
      if (isLogin) {
        await login(username, password);
		  console.log(user);
		  
      } else {
        await registration(username, password);
        Alert.alert("User successfully created");
      }
    } catch (e: any) {
      Alert.alert(e.response.data!.message);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{isLogin ? "Sing in" : "Sign up"}</Text>
      <TextInput
        style={styles.input}
        onChangeText={(username) => setUsername(username)}
        placeholder="Username"
      />
      <TextInput
        secureTextEntry
        style={styles.input}
        onChangeText={(password) => setPassword(password)}
        placeholder="Password"
      />
      <Pressable style={styles.submitBtn} onPress={onSubmit}>
        <Text style={styles.submitBtnTitle}>
          {isLogin ? "Entry" : "Create account"}
        </Text>
      </Pressable>
      <View style={styles.choiceWrapper}>
        <Text style={styles.choiceTitle}>
          {isLogin ? "Don`t have an account?" : "Already have an account?"}
        </Text>
        <Pressable
          style={styles.choiceBtn}
          onPress={() => setIsLogin(!isLogin)}
        >
          <Text style={styles.choiceBtnTitle}>
            {isLogin ? "Sign up" : "Sign in"}
          </Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  title: {
    fontSize: 46,
    color: "#e4e3e3",
    fontWeight: "700",
    marginBottom: 20,
  },
  input: {
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    width: "80%",
    height: 50,
    marginBottom: 15,
  },
  submitBtn: {
    display: "flex",
    alignItems: "center",
    borderWidth: 1,
    padding: 5,
    backgroundColor: "#3b00a8",
    borderColor: "#e4e3e3",
    borderRadius: 20,
    width: "80%",
    height: 50,
    overflow: "hidden",
  },
  submitBtnTitle: {
    color: "#e4e3e3",
    fontSize: 26,
    fontWeight: "700",
  },
  choiceWrapper: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "flex-end",
    width: "65%",
    marginTop: 20,
  },
  choiceTitle: {
    color: "#e4e3e3",
    fontSize: 16,
    fontWeight: "300",
    marginRight: 10,
  },
  choiceBtn: {
    height: 23,
  },
  choiceBtnTitle: {
    color: "#e4e3e3",
    fontSize: 16,
    fontWeight: "600",
  },
});

export default Auth;
