import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { IUser } from "../interfaces/IUser";

interface UserState {
  loading: boolean;
  isAuth: boolean;
  user: IUser | null;
  error: string;
}

const initialState: UserState = {
  loading: false,
  isAuth: false,
  user: null,
  error: "",
};

const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    userLogin(state) {
      state.loading = true;

    },
    userLoginSuccess(state, action: PayloadAction<IUser>) {
      state.isAuth = true;
      state.loading = false;
      state.error = "";
      state.user = action.payload;
    },
    userLoginError(state, action: PayloadAction<string>) {
      state.loading = false;
      state.error = action.payload;
    },
	 userLogout(state) {
		state.loading = false
		state.isAuth = false
		state.user = null
		state.error = ''
	 }
  },
});

export const { userLogin, userLoginSuccess, userLoginError, userLogout } =
  userSlice.actions;
export default userSlice.reducer;
