import React, { FC, useEffect, useState } from "react";
import { FlatList, StyleSheet, Text, TextInput, View } from "react-native";
import { ITodo } from "../../interfaces/ITodo";
import ListItem from "./ListItem";
import { SwipeListView } from "react-native-swipe-list-view";
import { checkTodosDB, deleteTodosDB } from "../../http/todoAPI";
import { checkTodo, deleteTodo } from "../../store/todoSlice";
import { useAppDispatch } from "../../store/hooks";



const List: FC<ListProps> = () => {
  const [todoId, setTodoId] = useState<string>("");
  const dispatch = useAppDispatch();

  const onRightAction = (id: string) => {
	  // This fnc return ListItem ID
    setTodoId(id);
  };

  const renderHiddenItem = () => (
	<View>
	  <Text>Background field</Text>
	</View>
 )
  useEffect(() => {
    const onDelete = async (id: string) => {
      dispatch(deleteTodo(id));
      await deleteTodosDB(id);
    };
    !!todoId && onDelete(todoId);
  }, [todoId]);

  return (
    <View style={styles.container}>
      <SwipeListView
        data={todos}
        renderItem={({ item }) => <ListItem todo={item} />}
        keyExtractor={({ id }) => id}
        renderHiddenItem={renderHiddenItem}
        useNativeDriver={false}
        rightActivationValue={-150}
		  rightActionValue={-500}
        onRightAction={onRightAction}
		  disableRightSwipe
      />
    </View>
  );
};
const styles = StyleSheet.create({
	container: {
	  flex: 1 ,
	  flexDirection: "row",
	  alignItems: "center",
	 
	  marginBottom: 15,
	  padding: 10,
	  height: 40,
	  width: "100%",
	},

 });

export default List;
