import ListItem from "./ListItem";
import { SwipeListView } from "react-native-swipe-list-view";
import { checkTodosDB, deleteTodosDB } from "../../http/todoAPI";
import { checkTodo, deleteTodo } from "../../store/todoSlice";
import { useAppDispatch } from "../../store/hooks";

interface ListProps {
  todos: ITodo[];
}

const List: FC<ListProps> = ({ todos }) => {
	const dispatch = useAppDispatch();

  const onRightAction = (id) => {
	console.log(id);
	// dispatch(deleteTodo(rowKey));
   //  await deleteTodosDB(todo);
  };

  return (
    <View>
      <SwipeListView
        data={todos}
        renderItem={({ item }) => <ListItem todo={item} />}
        keyExtractor={({ id }) => id}
        renderHiddenItem={() => (
          <View>
            <Text>Delete</Text>
          </View>
        )}
        useNativeDriver
        rightActivationValue={-150}
        onRightAction
      />
    </View>
  );
};

