import { authInstance } from "./index";
import { AppDispatch } from "../store/store";
import { fetchTodoError, fetchTodos, fetchTodosSuccess, addTodo, deleteTodo, checkTodo } from "../store/todoSlice";
import { ITodo } from "../interfaces/ITodo";
import { IUser } from "../interfaces/IUser";

export const getTodos = ({id}: Pick<IUser, "id">) => {
  return async (dispatch: AppDispatch) => {
    try {
      dispatch(fetchTodos());
      const { data } = await authInstance.get<ITodo[]>(
        `/api/todos/getTodos/${id}`
      );
      dispatch(fetchTodosSuccess(data));
    } catch (e: any) {
      dispatch(fetchTodoError(e));
    }
  };
};

export const addTodos = (todo: ITodo) => {
	return async (dispatch: AppDispatch) => {
	  try {
		 dispatch(addTodo(todo));
		 const response = await authInstance.post<ITodo>(
			`/api/todos/addTodo`, todo
		 );
	  } catch (e: any) {
		 dispatch(fetchTodoError(e.response.data.messeage));
	  }
	};
 };


 export const checkTodos = async ({id, checked}: Pick<ITodo, "id" | "checked">) => {
	  try {
		 const response = await authInstance.post(
			`/api/todos/checkTodo`, {
				id,
				checked
			}
		 );
	  } catch (e: any) {
		return e.response.data.messeage
	  }
 };

 export const deleteTodos = async (id: Pick<ITodo, "id">) => {
	  try {
		 const response = await authInstance.delete<ITodo>(
			`/api/todos/deleteTodo/${id}`
		 );
	  } catch (e: any) {
		return e.response.data.messeage
	  }
	
 };
