import { authInstance } from "./index";
import { ITodo } from "../interfaces/ITodo";
import { IUser } from "../interfaces/IUser";

export const getTodosDB = async ({ id }: Pick<IUser, "id">): Promise<ITodo[]> => {
    const { data } = await authInstance.get<ITodo[]>(
      `/api/todos/getTodos/${id}`
    );
    return data; 
};

export const addTodosDB = async (todo: ITodo) => {
    await authInstance.post<ITodo>(`/api/todos/addTodo`, todo);
};

export const checkTodosDB = async ({
  id,
  checked,
}: Pick<ITodo, "id" | "checked">) => {
  try {
    await authInstance.post(`/api/todos/checkTodo`, {
      id,
      checked,
    });
  } catch (e: any) {
    return e.response.data.messeage;
  }
};

export const deleteTodosDB = async ({ id }: Pick<ITodo, "id">) => {
  try {
    await authInstance.delete<ITodo>(`/api/todos/deleteTodo/${id}`);
  } catch (e: any) {
    return e.response.data.messeage;
  }
};
