import { authInstance } from "./index";
import { AppDispatch } from "../store/store";
import { fetchError, fetchTodos, fetchTodosSuccess } from "../store/todoSlice";
import { ITodo } from "../interfaces/ITodo";

export const getTodos = (author: string) => {
  return async (dispatch: AppDispatch) => {
    try {
      dispatch(fetchTodos());
      const { data } = await authInstance.get<ITodo[]>(
        `/api/todos/getTodos/${author}`
      );
      dispatch(fetchTodosSuccess(data));
    } catch (e) {
		 dispatch(fetchError)
	 }
  };
};
