import React, { FC } from "react";
import { Button, StyleSheet, Text, View } from "react-native";
import { ITodo } from "../interfaces/ITodo";
import Checkbox from "react-native-bouncy-checkbox";
import { useAppDispatch } from "../store/hooks";
import { checkTodos, deleteTodos } from "../http/todoAPI";

interface TodoProps {
  todo: ITodo;
}
const ListItem: FC<TodoProps> = ({ todo }) => {
  const { id, checked, title } = todo;
  const dispatch = useAppDispatch();

  const onCheck = () => {
	  dispatch(checkTodos(todo))
	  console.log('click', todo);
	  
	}

  return (
    <View style={styles.container} >
      <Checkbox
        isChecked={checked}
        useNativeDriver={false}
        onPress={onCheck}
      />
      <Text style={styles.title}>{title}</Text>
		<Button title='Delete' onPress={onCheck}/>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    borderColor: "#e4e3e3",
    borderWidth: 1,
    borderRadius: 10,
    marginBottom: 15,
    padding: 10,
    height: 40,
    width: "100%",
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
    color: "#e4e3e3",
  },
  input: {
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    width: "100",
    height: 50,
    marginBottom: 15,
  },
});

export default ListItem;
