import React, { FC, useEffect } from "react";
import { FlatList, StyleSheet, Text, TextInput, View } from "react-native";
import { ITodo } from "../../interfaces/ITodo";
import ListItem from "./ListItem";
import { SwipeListView } from "react-native-swipe-list-view";
import { checkTodosDB, deleteTodosDB } from "../../http/todoAPI";
import { checkTodo, deleteTodo } from "../../store/todoSlice";
import { useAppDispatch } from "../../store/hooks";

interface ListProps {
  todos: ITodo[];
}

const List: FC<ListProps> = ({ todos }) => {
	const dispatch = useAppDispatch();

	useEffect(() => {
		const onRightAction = async (id: string) => {
			dispatch(deleteTodo(id));
			 await deleteTodosDB(id);
		  };

		  return onRightAction
})
  

  return (
    <View>
      <SwipeListView
        data={todos}
        renderItem={({ item }) => <ListItem todo={item} />}
        keyExtractor={({ id }) => id}
        renderHiddenItem={() => (
          <View>
            <Text>Delete</Text>
          </View>
        )}
        useNativeDriver
        rightActivationValue={-150}
        onRightAction={onRightAction}
      />
    </View>
  );
};

