import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { IUser } from "../interfaces/IUser";

interface UserState {
  loading: boolean;
  isAuth: boolean;
  
  user: IUser | null;
  userError: string;
  userCheckAuth: string
}

const initialState: UserState = {
  loading: false,
  isAuth: false,
  user: null,
  userError: "",
  userCheckAuth:''
};

const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    userLogin(state) {
      state.loading = true;
    },
    userLoginSuccess(state, action: PayloadAction<IUser>) {
      state.isAuth = true;
      state.loading = false;
      state.userError = "";
      state.user = action.payload;
    },
    fetchUserError(state, action: PayloadAction<string>) {
      state.loading = false;
      state.userError = action.payload;
    },
	 userCheckAuth(state, action: PayloadAction<string>) {
      state.loading = false;
      state.userError = action.payload;
    },
	 userLogout(state) {
		state.isAuth = false
		state.user = null
		state.userError = ''
	 }
  },
});

export const { userLogin, userLoginSuccess, fetchUserError, userLogout, userCheckAuth } =
  userSlice.actions;
export default userSlice.reducer;
