import {
  Button,
  Pressable,
  StyleSheet,
  Text,
  View,
} from "react-native";
import { Colors } from "../../styles/colors";
import PriorityCircle from "../PriorityCircle";

const RowOptions = (title, number) => {	
  return (
    <View style={styles.modalRow}>
		 <PriorityCircle priority={number+1}/>
      <Text style={styles.optionTxt}>{title}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  modalRow: {
    display: "flex",
    flexDirection: "row",
	 alignItems: 'center',
  },
  optionCircle: {
    overflow: "hidden",
    width: 20,
    height: 20,
    borderWidth: 5,
    borderRadius: 8,
	 marginRight: 5,
  },
  optionTxt: {
	  fontSize: 24,
	  fontWeight: '500',
	  color: Colors.black
  }
});

export default RowOptions;
