import AsyncStorage from "@react-native-async-storage/async-storage";
import { instance, authInstance } from "./index";
import jwtDecode from "jwt-decode";
import { AppDispatch } from "../store/store";

export const registration = async (
  username: string | number,
  password: string | number
) => {
  const { data } = await instance.post("/api/user/registration", {
    username,
    password,
  });
  AsyncStorage.setItem("token", data.token);
  return jwtDecode(data.token);
};

export const login = (username: string | number, password: string | number) => {
  return async (dispatch: AppDispatch) => {
	  try {
		  dispatch
		const { data } = await instance.post("/api/user/login", {
			username,
			password,
		 });
		 AsyncStorage.setItem("token", data.token);
	
		 return jwtDecode(data.token);
	  } catch (e) {
		  
	  }

  };
};

export const check = async () => {
  const { data } = await authInstance.get("/api/user/auth");
  AsyncStorage.setItem("token", data.token);
  return jwtDecode(data.token);
};
