import React, { FC, useEffect, useState } from "react";
import { Button, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { ITodo } from "../../interfaces/ITodo";
import Checkbox from "react-native-bouncy-checkbox";
import { useAppDispatch } from "../../store/hooks";
import { checkTodosDB, deleteTodosDB } from "../../http/todoAPI";
import { checkTodo, deleteTodo } from "../../store/todoSlice";

interface TodoProps {
  todo: ITodo,
  onRightAction: () => void
}

const ListItem: FC<TodoProps> = ({ todo, onRightAction }) => {
  const { id, checked, title } = todo;
  // const [check, setCheck] = useState(checked)
  const dispatch = useAppDispatch();

  const onCheck = () => {
    dispatch(checkTodo(id));
  };
//   const onRightAction = async () => {
//     dispatch(deleteTodo(id));
//     await deleteTodosDB(todo);
//   };

  useEffect(() => {
    checkTodosDB(todo);
  }, [checked]);

  useEffect(() => {
	onRightAction(id, todo)
 }, []);
  return (
    <TouchableOpacity>
      <View style={styles.container}>
        <Checkbox
          isChecked={checked}
          useNativeDriver={false}
          onPress={onCheck}
        />
        <Text style={styles.title}>{title}</Text>
        <Button title="Delete" onPress={onRightAction} />
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    borderColor: "#e4e3e3",
    borderWidth: 1,
    borderRadius: 10,
    marginBottom: 15,
    padding: 10,
    height: 40,
    width: "100%",
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
    color: "#e4e3e3",
  },
  input: {
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    width: "100",
    height: 50,
    marginBottom: 15,
  },
});

export default ListItem;
