import { useEffect, useState } from "react";
import { Button, StyleSheet, Text, TextInput, View } from "react-native";
import { addTodosDB } from "../http/todoAPI";
import { ITodo } from "../interfaces/ITodo";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { addTodo, fetchTodoError } from "../store/todoSlice";

const TodoForm = () => {
  const [value, setValue] = useState<string>("");
  const [isOpen, setIsOpen] = useState<b>("");

  const { user } = useAppSelector((state) => state.user);
  const dispatch = useAppDispatch();

  const createTodo = async () => {
    try {
		 if (value && user) {
			const newTodo: ITodo = {
				id: Date.now().toString(),
				author: user?.id,
				checked: false,
				title: value,
				priority: 'High'
			 };
			 dispatch(addTodo(newTodo));
			 await addTodosDB(newTodo);
			 setValue("");
		 }
		 return console.log("Todo must have title");
		 
    } catch (error: any) {
		dispatch(fetchTodoError(error));  
    }
  };

  return (
    <View style={styles.container}>
      <TextInput
        onChangeText={(text) => setValue(text)}
        value={value}
        style={styles.input}
      />
      <Button color={"#3b00a8"} title="Add todo" onPress={createTodo} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    alignItems: "center",
    marginBottom: 15,
  },
  title: {
    fontSize: 30,
    fontWeight: "700",
    color: "#e4e3e3",
  },
  input: {
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    width: "100",
    height: 50,
    marginBottom: 15,
  },
});

export default TodoForm;
