import dayjs from "dayjs";
import { useEffect, useState } from "react";
import {
  Button,
  Pressable,
  StyleSheet,
  Text,
  TextInput,
  View,
} from "react-native";
import { Ionicons, Entypo, MaterialCommunityIcons } from "@expo/vector-icons";
import ModalDropdown from "react-native-modal-dropdown";
import { globalStyles } from "../styles/global";
import { addTodosDB } from "../http/todoAPI";
import { ITodo } from "../interfaces/ITodo";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { addTodo, fetchTodoError } from "../store/todoSlice";

const todoPriority = ["High", "Middle", "Low", "None"];

const TodoForm = () => {
  const [value, setValue] = useState<string>("");
  const [priority, setPriority] = useState<number>(4);
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const { user } = useAppSelector((state) => state.user);
  const dispatch = useAppDispatch();

  const createTodo = async () => {
    try {
      if (value && user) {
        const newTodo: ITodo = {
          id: Date.now().toString(),
          author: user.id,
          checked: false,
          title: value,
          priority: priority,
          date: `${dayjs().format("D")} ${dayjs().format("MMM")}`,
          addDate: dayjs().format(),
        };
        dispatch(addTodo(newTodo));
        await addTodosDB(newTodo);
        setValue("");
        setIsOpen(!isOpen);
        return;
      }
      return console.log("Todo must have title");
    } catch (error: any) {
      dispatch(fetchTodoError(error));
    }
  };

  return (
    <View>
      {isOpen ? (
        <View style={styles.formOpen}>
          <TextInput
            onChangeText={(text) => setValue(text)}
            value={value}
            placeholder={"Enter you task..."}
            style={styles.input}
          />
          <ModalDropdown
            options={todoPriority}
            onSelect={(idx) => setPriority(+idx + 1)}
            style={styles.modalBtn}
            dropdownStyle={styles.dropdownList}
				dropdownTextStyle={styles.dropdownText}
          >
            <MaterialCommunityIcons
              name="priority-high"
              size={24}
              color="black"
            />
          </ModalDropdown>
          <Pressable
            style={[globalStyles.primaryBg, styles.addBtn]}
            onPress={createTodo}
          >
            <Entypo name="add-to-list" size={24} color="#FFF" />
          </Pressable>
        </View>
      ) : (
        <Pressable
          style={[styles.formClose, globalStyles.primaryBg]}
          onPress={() => setIsOpen(!isOpen)}
        >
          <Ionicons name="add" size={42} color="#FFF" />
        </Pressable>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  formOpen: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 10,
    marginBottom: 15,
  },
  formClose: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    alignSelf: "flex-end",
    overflow: "hidden",
    borderWidth: 1,
    width: 80,
    height: 80,
    borderRadius: 50,
    borderColor: "#9381FF",
    marginRight: 15,
    marginBottom: 15,
  },
  input: {
    flex: 1,
    padding: 10,
    backgroundColor: "#e4e3e3",
    borderRadius: 20,
    height: 60,
    marginRight: 5,
    maxWidth: 500,
  },
  modalBtn: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: 60,
    width: 60,
    borderWidth: 1,
    borderRadius: 15,
    marginRight: 5,
  },
  dropdownList: {
    position: "relative",
    bottom: 0,
    left: 0,
    flex: 1,
	 alignItems: 'center',
    width: "100%",
	 
  },
  dropdownText: {
	 fontSize: 24,
	 textDecorationLine: ''
  },
  addBtn: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: 60,
    width: 60,
    borderRadius: 15,
  },
});

export default TodoForm;
