import dayjs from "dayjs";
import { useState } from "react";
import { Pressable, StyleSheet, TextInput, View } from "react-native";
import { Ionicons, Entypo, MaterialCommunityIcons } from "@expo/vector-icons";
import ModalDropdown from "react-native-modal-dropdown";
import { addTodosDB } from "../../http/todoAPI";
import { ITodo } from "../../interfaces/ITodo";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { addTodo, fetchTodoError } from "../../store/todoSlice";
import RowOptions from "./RowOptions";
import { Colors } from "../../styles/colors";

const todoPriority = [
  { title: "High", number: 1 },
  { title: "Middle", number: 2 },
  { title: "Low", number: 3 },
  { title: "None", number: 4 },
];

const TodoForm = () => {
  const [value, setValue] = useState<string>("");
  const [priority, setPriority] = useState<number>(todoPriority[3].number);
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const { user } = useAppSelector((state) => state.user);
  const dispatch = useAppDispatch();

  const createTodo = async () => {
    try {
      if (value && user) {
        const newTodo: ITodo = {
          id: Date.now().toString(),
          author: user.id,
          checked: false,
          title: value,
          priority: priority,
          addDate: dayjs().format(),
        };
        dispatch(addTodo(newTodo));
        await addTodosDB(newTodo);
        setValue("");
        return;
      }
      return console.log("Todo must have title");
    } catch (error: any) {
      dispatch(fetchTodoError(error));
    }
  };

  return (
    <View style={styles.container}>
      {isOpen ? (
        <View style={styles.formOpen}>
			  <View>

			  </View>
          
        </View>
      ) : (
        <Pressable style={styles.formClose} onPress={() => setIsOpen(!isOpen)}>
          <Ionicons name="add" size={42} color={Colors.white} />
        </Pressable>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    alignSelf: "flex-end",
  },
  formOpen: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 10,
    marginBottom: 15,
  },
  formClose: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderWidth: 1,
    width: 80,
    height: 80,
    borderRadius: 50,
    borderColor: Colors.primaryBg,
    backgroundColor: Colors.primaryBg,
    marginRight: 15,
    marginBottom: 15,
  },
  input: {
    flex: 1,
    padding: 10,
    backgroundColor: Colors.white,
    borderRadius: 15,
    height: 60,
    marginRight: 5,
    maxWidth: 500,
    fontSize: 18,
  },
  modalBtn: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: 60,
    width: 60,
    borderWidth: 1,
    borderRadius: 15,
    marginRight: 5,
  },

  dropdownList: {
    position: "relative",
    bottom: 0,
    left: 0,
    flex: 1,
    width: "100%",
    backgroundColor: Colors.background,
    paddingHorizontal: 40,
  },

  addBtn: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: 60,
    width: 60,
    borderRadius: 15,
    backgroundColor: Colors.primaryBg,
  },
});

export default TodoForm;
