import AsyncStorage from "@react-native-async-storage/async-storage";
import { instance, authInstance } from "./index";
import jwtDecode from "jwt-decode";
import { AppDispatch } from "../store/store";
import {
  userLogin,
  userLoginSuccess,
  fetchUserError,
  userLogout,
} from "../store/userSlice";

export const registration = async (
  username: string | number,
  password: string | number
) => {
  try {
    const { data } = await instance.post("/api/user/registration", {
      username,
      password,
    });
    await AsyncStorage.setItem("token", data.token);
    return jwtDecode(data.token);
  } catch (e: any) {
    return e.response.data.message;
  }
};

export const login = async (
  username: string | number,
  password: string | number
) => {
  try {
    const { data } = await instance.post("/api/user/login", {
      username,
      password,
    });
    await AsyncStorage.setItem("token", data.token);
    return jwtDecode(data.token);
  } catch (e: any) {
    return e.response.data.message;
  }
};

export const logout = async() => {
   await AsyncStorage.removeItem("token");
    dispatch(userLogout());
};

export const check = () => {
  return async (dispatch: AppDispatch) => {
    dispatch(userLogin());
    const { data } = await authInstance.get("/api/user/auth");
    await AsyncStorage.setItem("token", data.token);
    dispatch(userLoginSuccess(jwtDecode(data.token)));
  };
};
