import {
	Button,
	Pressable,
	StyleSheet,
	Text,
	TextInput,
	View,
 } from "react-native";
import { globalStyles } from "../../styles/global";


  const RowOptions = (options) => {
	return (
	  <View style={styles.modalRow}>
			 <View style={[
			  options === "High" && globalStyles.redBg,
			  options === "Middle" && globalStyles.yellowBg,
			  options === "None" && globalStyles.blueBg,
			  options === "Low" && globalStyles.greenBg,
			 styles.optionCircle
			]}/>
		 <Text>{options}</Text>
	  </View>
	);
 }

 const styles = StyleSheet.create({
	formOpen: {
	  display: "flex",
	  flexDirection: "row",
	  alignItems: "center",
	  justifyContent: "center",
	  paddingHorizontal: 10,
	  marginBottom: 15,
	},
	formClose: {
	  display: "flex",
	  alignItems: "center",
	  justifyContent: "center",
	  alignSelf: "flex-end",
	  overflow: "hidden",
	  borderWidth: 1,
	  width: 80,
	  height: 80,
	  borderRadius: 50,
	  borderColor: "#9381FF",
	  marginRight: 15,
	  marginBottom: 15,
	},
	input: {
	  flex: 1,
	  padding: 10,
	  backgroundColor: "#e4e3e3",
	  borderRadius: 20,
	  height: 60,
	  marginRight: 5,
	  maxWidth: 500,
	},
	modalBtn: {
	  display: "flex",
	  alignItems: "center",
	  justifyContent: "center",
	  height: 60,
	  width: 60,
	  borderWidth: 1,
	  borderRadius: 15,
	  marginRight: 5,
	},
  
	dropdownList: {
	  position: "relative",
	  bottom: 0,
	  left: 0,
	  flex: 1,
	  alignItems: "center",
	  width: "100%",
	  textDecorationLine: "none",
	},
	modalRow: {
	 display: "flex",
	 flexDirection: "row",
  },
  optionCircle: {
	overflow: 'hidden',
	width: 10,
	height: 10,
	borderWidth: 1,
	borderRadius: 50
  },
 
	addBtn: {
	  display: "flex",
	  alignItems: "center",
	  justifyContent: "center",
	  height: 60,
	  width: 60,
	  borderRadius: 15,
	},
 });

 export default RowOptions
