import React, { useEffect } from "react";
import {
  View,
  StyleSheet,
  Text,
  TextInput,
  Pressable,
  Button,
} from "react-native";
import List from "../components/List";
import TodoForm from "../components/TodoForm";
import { getTodos } from "../http/todoAPI";
import { logout } from "../http/userAPI";
import { useAppDispatch, useAppSelector } from "../store/hooks";

const Todo = () => {
  const { loading, user, userError } = useAppSelector((state) => state.user);
  const { loadingTodos, todos, todoError } = useAppSelector((state) => state.todo);
  const dispatch = useAppDispatch();
  console.log(loadingTodos, todos, todoError);

  useEffect(() => {
	user && dispatch(getTodos(user.id))
  }, [])

  if (!!loading || !!loadingTodos) {
	  return (
<Text>Loading...</Text>
	  )
  } 

  return (
    <View>
      {!!todoError && <Text>{todoError}</Text>}
      {!!userError && <Text>{userError}</Text>}
      {/* {!!loading || !!loadingTodos && <Text>Loading...</Text>} */}
      <TodoForm />
      <List todos={todos}/>
      <Button title="Log out" onPress={() => dispatch(logout())} />
    </View>
  );
};

export default Todo;
