import axios from "axios";
const TODO_APP_API_URL = "localhost:5000/api";

const instance = axios.create({
  baseURL: "localhost:4000/api",
});

export const registration = async (
  username: string | number,
  password: string | number
) => {
  try {
    const response = instance.post("/user/registration", {
      username,
      password,
    });
    return response;
  } catch (e) {
    console.log("error", e);
  }
};

export const login = async (
  username: string | number,
  password: string | number
) => {
  try {
    const response = instance.post("/user/login", {
      username,
      password,
    });
    return response;
  } catch (e) {
    console.log("error", e);
  }
};
