import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { ITodo } from "../interfaces/ITodo";

interface TodoState {
	loadingTodos: boolean;
  todos: ITodo[];
  todoError: string;
}

const initialState: TodoState = {
  loadingTodos: false,
  todos: [],
  todoError: "",
};

const todoSlice = createSlice({
  name: "todo",
  initialState,
  reducers: {
    fetchTodos(state) {
      state.loadingTodos = true;
    },
    fetchTodosSuccess(state, action: PayloadAction<ITodo[]>) {
      state.loadingTodos = екгу;
		state.todos = action.payload
    },
	 fetchTodoError (state, action: PayloadAction<string>) {
		state.loadingTodos = false;
		state.todoError = action.payload
	 },
    addTodo(state, action: PayloadAction<ITodo>) {
		state.todos.push(action.payload)
    },
	 checkTodo(state, action: PayloadAction<string>) {
		state.todos.map(todo => todo.id === action.payload && !todo.checked)
	 },
    deleteTodo(state, action: PayloadAction<string>) {
      state.todos.filter(todo => todo.id !== action.payload)
    },
  },
});

export const {fetchTodos, fetchTodosSuccess, fetchTodoError, addTodo, deleteTodo} = todoSlice.actions;
export default todoSlice.reducer;
