import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  Button,
  Text,
  TextInput,
  View,
  Pressable,
} from "react-native";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { logout } from "../http/userAPI";
import { userLogout } from "../store/userSlice";
import ModalDropdown from "react-native-modal-dropdown";
import { sortByDate, sortByPriority } from "../store/todoSlice";
import dayjs from "dayjs";
import { Colors } from "../styles/colors";

const sortOptions = ["Date", "Priority"];

export const Header = () => {
  const [sort, setSort] = useState<string>("");
  const dispatch = useAppDispatch();

  const onLogout = async () => {
    logout();
    dispatch(userLogout());
  };

  useEffect(() => {
    if (sort === "Priority") {
      dispatch(sortByPriority());
      return;
    }
    if (sort === "Date") {
      dispatch(sortByDate());
      return;
    }
  }, [sort]);
  return (
    <View style={styles.container}>
      <View style={styles.titleWrapper}>
        <Text style={styles.title}>Today {dayjs().format("DD MMM")}</Text>
      </View>
      <ModalDropdown
        defaultValue="Sort by"
        renderButtonText={() => "Sort by"}
        textStyle={styles.modalTitle}
        options={sortOptions}
        onSelect={(idx, value) => setSort(value)}
        style={styles.sortModal}
		  dr
      />
      <Pressable onPress={onLogout}>
        <Text style={styles.exitTxt}>Exit</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    borderBottomWidth: 1,
    borderBottomEndRadius: 5,
    borderBottomStartRadius: 5,
    borderColor: "#F8F7FF",
    paddingTop: 25,
    paddingHorizontal: 15,
    paddingBottom: 10,
    width: "100%",
    backgroundColor: Colors.primaryBg,
  },
  titleWrapper: {
    flex: 1,
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
    color: Colors.white,
  },
  sortModal: {
    borderWidth: 1,
    borderRadius: 5,
    borderColor: "#F8F7FF",
    marginRight: 15,
    paddingVertical: 3,
    paddingHorizontal: 5,
    backgroundColor: Colors.background,
  },
  modalTitle: { 
	  fontSize: 14, 
	  fontWeight: "500", 
	  color: Colors.black 
	},

  exitTxt: {
    color: Colors.white,
    fontWeight: "500",
  },
});
