import React, { useState } from "react";
import { View, StyleSheet, Text, TextInput, Button } from "react-native";
import { registration } from "../http/fetch";

const Auth = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isAuth, setIsAuth] = useState(false);

  const onRegistration = (name: string | number, password: string | number) => {
	registration(name, password)
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{isAuth ? 'Sing in' : 'Sign up'}</Text>
      <TextInput style={styles.input} onChangeText={username => setUsername(username)} placeholder='Username'/>
      <TextInput secureTextEntry style={styles.input} onChangeText={password => setPassword(password)} placeholder='Password'/>
		<Button onPress={() => onRegistration()} title='Create'/>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
	 width: '100%'
  },
  title: {
    fontSize: 36,
    color: "#e4e3e3",
    fontWeight: "700",
	 marginBottom: 20,
  },
  input: {
	padding: 10,
	backgroundColor: '#e4e3e3',
	borderRadius: 5,
	width: '80%',
	height: 50,
	marginBottom: 15,
  }
});

export default Auth;
