  return (
    <View>
      <SwipeListView
        data={todos}
        renderItem={({ item }) => <ListItem todo={item} />}
        keyExtractor={({ id }) => id}
        renderHiddenItem={() => (
          <View>
            <Text>Delete</Text>
          </View>
        )}
        leftOpenValue={0}
        //   rightOpenValue={-75}
        rightActivationValue={100}
        onRightAction={onRightAction}
		  useNa
      />
    </View>
  );
};

